package com.tinhtx.player.local.database.dao

import androidx.room.*
import com.tinhtx.player.local.database.entities.MediaEntity
import kotlinx.coroutines.flow.Flow

@Dao
interface MediaDao {

    @Query("SELECT * FROM media_items")
    fun getAllMediaItems(): Flow<List<MediaEntity>>

    @Query("SELECT * FROM media_items WHERE mediaType = 'AUDIO'")
    fun getAudioItems(): Flow<List<MediaEntity>>

    @Query("SELECT * FROM media_items WHERE mediaType = 'VIDEO'")
    fun getVideoItems(): Flow<List<MediaEntity>>

    @Query("SELECT * FROM media_items WHERE id = :id")
    fun getMediaItemById(id: String): Flow<MediaEntity?>

    @Query("SELECT * FROM media_items WHERE title LIKE '%' || :query || '%' OR artist LIKE '%' || :query || '%' OR album LIKE '%' || :query || '%'")
    fun searchMediaItems(query: String): Flow<List<MediaEntity>>

    @Query("SELECT * FROM media_items WHERE album = :albumId")
    fun getMediaItemsByAlbum(albumId: String): Flow<List<MediaEntity>>

    @Query("SELECT * FROM media_items WHERE artist = :artistId")
    fun getMediaItemsByArtist(artistId: String): Flow<List<MediaEntity>>

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertMediaItem(mediaItem: MediaEntity)

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertMediaItems(mediaItems: List<MediaEntity>)

    @Update
    suspend fun updateMediaItem(mediaItem: MediaEntity)

    @Delete
    suspend fun deleteMediaItem(mediaItem: MediaEntity)

    @Query("DELETE FROM media_items")
    suspend fun deleteAllMediaItems()
}

package com.tinhtx.player.local.database.dao

import androidx.room.*
import com.tinhtx.player.local.database.entities.PlaylistEntity
import com.tinhtx.player.local.database.entities.PlaylistMediaEntity
import com.tinhtx.player.local.database.entities.MediaEntity
import kotlinx.coroutines.flow.Flow

@Dao
interface PlaylistDao {

    @Query("SELECT * FROM playlists")
    fun getAllPlaylists(): Flow<List<PlaylistEntity>>

    @Query("SELECT * FROM playlists WHERE id = :id")
    fun getPlaylistById(id: String): Flow<PlaylistEntity?>

    @Query("""
        SELECT m.* FROM media_items m
        INNER JOIN playlist_media pm ON m.id = pm.mediaId
        WHERE pm.playlistId = :playlistId
        ORDER BY pm.position
    """)
    fun getPlaylistItems(playlistId: String): Flow<List<MediaEntity>>

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertPlaylist(playlist: PlaylistEntity)

    @Update
    suspend fun updatePlaylist(playlist: PlaylistEntity)

    @Delete
    suspend fun deletePlaylist(playlist: PlaylistEntity)

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertPlaylistMedia(playlistMedia: PlaylistMediaEntity)

    @Query("DELETE FROM playlist_media WHERE playlistId = :playlistId AND mediaId = :mediaId")
    suspend fun removePlaylistMedia(playlistId: String, mediaId: String)

    @Query("DELETE FROM playlist_media WHERE playlistId = :playlistId")
    suspend fun clearPlaylist(playlistId: String)

    @Query("UPDATE playlist_media SET position = :newPosition WHERE playlistId = :playlistId AND mediaId = :mediaId")
    suspend fun updateMediaPosition(playlistId: String, mediaId: String, newPosition: Int)
}

package com.tinhtx.player.local.database.entities
import androidx.room.Entity import androidx.room.PrimaryKey
@Entity(tableName = "media_items") data class MediaEntity( @PrimaryKey val id: String, val title: String, val artist: String, val album: String, val duration: Long, val uri: String, val artworkUri: String? = null, val mediaType: String, val size: Long = 0, val dateAdded: Long = 0, val path: String = "", val mimeType: String = "" )

package com.tinhtx.player.local.database.entities
import androidx.room.Entity import androidx.room.PrimaryKey
@Entity(tableName = "playlists") data class PlaylistEntity( @PrimaryKey val id: String, val name: String, val description: String = "", val artworkUri: String? = null, val trackCount: Int = 0, val duration: Long = 0, val dateCreated: Long = System.currentTimeMillis(), val dateModified: Long = System.currentTimeMillis() )

package com.tinhtx.player.local.database.entities
import androidx.room.Entity import androidx.room.ForeignKey import androidx.room.Index
@Entity( tableName = "playlist_media", primaryKeys = ["playlistId", "mediaId"], foreignKeys = [ ForeignKey( entity = PlaylistEntity::class, parentColumns = ["id"], childColumns = ["playlistId"], onDelete = ForeignKey.CASCADE ), ForeignKey( entity = MediaEntity::class, parentColumns = ["id"], childColumns = ["mediaId"], onDelete = ForeignKey.CASCADE ) ], indices = [ Index(value = ["playlistId"]), Index(value = ["mediaId"]) ] ) data class PlaylistMediaEntity( val playlistId: String, val mediaId: String, val position: Int, val dateAdded: Long = System.currentTimeMillis() )

package com.tinhtx.player.local.preferences
import android.content.Context import android.content.SharedPreferences import com.tinhtx.player.model.AppTheme import com.tinhtx.player.model.RepeatMode import com.tinhtx.player.model.UserSettings import javax.inject.Inject import javax.inject.Singleton
@Singleton class UserPreferencesManager @Inject constructor( context: Context ) { private val preferences: SharedPreferences = context.getSharedPreferences( "tinhtx_player_preferences", Context.MODE_PRIVATE )
fun getUserSettings(): UserSettings { return UserSettings( theme = AppTheme.valueOf(preferences.getString(KEY_THEME, AppTheme.SYSTEM.name) ?: AppTheme.SYSTEM.name), playbackSpeed = preferences.getFloat(KEY_PLAYBACK_SPEED, 1.0f), repeatMode = RepeatMode.valueOf(preferences.getString(KEY_REPEAT_MODE, RepeatMode.OFF.name) ?: RepeatMode.OFF.name), shuffleMode = preferences.getBoolean(KEY_SHUFFLE_MODE, false), autoPlayNext = preferences.getBoolean(KEY_AUTO_PLAY_NEXT, true), showNotification = preferences.getBoolean(KEY_SHOW_NOTIFICATION, true), equalizerEnabled = preferences.getBoolean(KEY_EQUALIZER_ENABLED, false), bassBoostLevel = preferences.getInt(KEY_BASS_BOOST, 0), virtualizerLevel = preferences.getInt(KEY_VIRTUALIZER, 0), skipSilence = preferences.getBoolean(KEY_SKIP_SILENCE, false), crossfadeDuration = preferences.getInt(KEY_CROSSFADE_DURATION, 0) ) }
fun saveUserSettings(settings: UserSettings) { preferences.edit().apply { putString(KEY_THEME, settings.theme.name) putFloat(KEY_PLAYBACK_SPEED, settings.playbackSpeed) putString(KEY_REPEAT_MODE, settings.repeatMode.name) putBoolean(KEY_SHUFFLE_MODE, settings.shuffleMode) putBoolean(KEY_AUTO_PLAY_NEXT, settings.autoPlayNext) putBoolean(KEY_SHOW_NOTIFICATION, settings.showNotification) putBoolean(KEY_EQUALIZER_ENABLED, settings.equalizerEnabled) putInt(KEY_BASS_BOOST, settings.bassBoostLevel) putInt(KEY_VIRTUALIZER, settings.virtualizerLevel) putBoolean(KEY_SKIP_SILENCE, settings.skipSilence) putInt(KEY_CROSSFADE_DURATION, settings.crossfadeDuration) apply() } }
companion object { private const val KEY_THEME = "theme" private const val KEY_PLAYBACK_SPEED = "playback_speed" private const val KEY_REPEAT_MODE = "repeat_mode" private const val KEY_SHUFFLE_MODE = "shuffle_mode" private const val KEY_AUTO_PLAY_NEXT = "auto_play_next" private const val KEY_SHOW_NOTIFICATION = "show_notification" private const val KEY_EQUALIZER_ENABLED = "equalizer_enabled" private const val KEY_BASS_BOOST = "bass_boost" private const val KEY_VIRTUALIZER = "virtualizer" private const val KEY_SKIP_SILENCE = "skip_silence" private const val KEY_CROSSFADE_DURATION = "crossfade_duration" } }

package com.tinhtx.player.mapper

import com.tinhtx.player.local.database.entities.MediaEntity
import com.tinhtx.player.model.MediaItem
import com.tinhtx.player.model.MediaType

object MediaMapper {

    fun toEntity(mediaItem: MediaItem): MediaEntity {
        return MediaEntity(
            id = mediaItem.id,
            title = mediaItem.title,
            artist = mediaItem.artist,
            album = mediaItem.album,
            duration = mediaItem.duration,
            uri = mediaItem.uri,
            artworkUri = mediaItem.artworkUri,
            mediaType = mediaItem.mediaType.name,
            size = mediaItem.size,
            dateAdded = mediaItem.dateAdded,
            path = mediaItem.path,
            mimeType = mediaItem.mimeType
        )
    }

    fun toDomain(entity: MediaEntity): MediaItem {
        return MediaItem(
            id = entity.id,
            title = entity.title,
            artist = entity.artist,
            album = entity.album,
            duration = entity.duration,
            uri = entity.uri,
            artworkUri = entity.artworkUri,
            mediaType = MediaType.valueOf(entity.mediaType),
            size = entity.size,
            dateAdded = entity.dateAdded,
            path = entity.path,
            mimeType = entity.mimeType
        )
    }

    fun toEntityList(mediaItems: List<MediaItem>): List<MediaEntity> {
        return mediaItems.map { toEntity(it) }
    }

    fun toDomainList(entities: List<MediaEntity>): List<MediaItem> {
        return entities.map { toDomain(it) }
    }
}

package com.tinhtx.player.mapper

import com.tinhtx.player.local.database.entities.PlaylistEntity
import com.tinhtx.player.model.PlaylistInfo

object PlaylistMapper {

    fun toEntity(playlistInfo: PlaylistInfo): PlaylistEntity {
        return PlaylistEntity(
            id = playlistInfo.id,
            name = playlistInfo.name,
            description = playlistInfo.description,
            artworkUri = playlistInfo.artworkUri,
            trackCount = playlistInfo.trackCount,
            duration = playlistInfo.duration,
            dateCreated = playlistInfo.dateCreated,
            dateModified = playlistInfo.dateModified
        )
    }

    fun toDomain(entity: PlaylistEntity): PlaylistInfo {
        return PlaylistInfo(
            id = entity.id,
            name = entity.name,
            description = entity.description,
            artworkUri = entity.artworkUri,
            trackCount = entity.trackCount,
            duration = entity.duration,
            dateCreated = entity.dateCreated,
            dateModified = entity.dateModified
        )
    }

    fun toEntityList(playlists: List<PlaylistInfo>): List<PlaylistEntity> {
        return playlists.map { toEntity(it) }
    }

    fun toDomainList(entities: List<PlaylistEntity>): List<PlaylistInfo> {
        return entities.map { toDomain(it) }
    }
}

package com.tinhtx.player.repository

import com.tinhtx.player.common.Resource
import com.tinhtx.player.local.database.dao.MediaDao
import com.tinhtx.player.mapper.MediaMapper
import com.tinhtx.player.model.MediaItem
import com.tinhtx.player.model.AlbumInfo
import com.tinhtx.player.model.ArtistInfo
import com.tinhtx.player.model.SortOrder
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.map
import kotlinx.coroutines.flow.catch
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class MediaRepositoryImpl @Inject constructor(
    private val mediaDao: MediaDao
) : MediaRepository {

    override fun getAllMediaItems(): Flow<Resource<List<MediaItem>>> {
        return mediaDao.getAllMediaItems()
            .map { entities ->
                Resource.Success(MediaMapper.toDomainList(entities))
            }
            .catch { e ->
                emit(Resource.Error("Failed to get media items: ${e.message}"))
            }
    }

    override fun getAudioItems(): Flow<Resource<List<MediaItem>>> {
        return mediaDao.getAudioItems()
            .map { entities ->
                Resource.Success(MediaMapper.toDomainList(entities))
            }
            .catch { e ->
                emit(Resource.Error("Failed to get audio items: ${e.message}"))
            }
    }

    override fun getVideoItems(): Flow<Resource<List<MediaItem>>> {
        return mediaDao.getVideoItems()
            .map { entities ->
                Resource.Success(MediaMapper.toDomainList(entities))
            }
            .catch { e ->
                emit(Resource.Error("Failed to get video items: ${e.message}"))
            }
    }

    override fun getMediaItemById(id: String): Flow<Resource<MediaItem?>> {
        return mediaDao.getMediaItemById(id)
            .map { entity ->
                Resource.Success(entity?.let { MediaMapper.toDomain(it) })
            }
            .catch { e ->
                emit(Resource.Error("Failed to get media item: ${e.message}"))
            }
    }

    override fun getAlbums(): Flow<Resource<List<AlbumInfo>>> {
        // This would be implemented with proper album aggregation logic
        return getAllMediaItems().map { resource ->
            when (resource) {
                is Resource.Success -> {
                    val albums = resource.data?.groupBy { it.album }?.map { (albumName, items) ->
                        AlbumInfo(
                            id = items.first().album,
                            name = albumName,
                            artist = items.first().artist,
                            artworkUri = items.first().artworkUri,
                            trackCount = items.size,
                            duration = items.sumOf { it.duration }
                        )
                    } ?: emptyList()
                    Resource.Success(albums)
                }
                is Resource.Error -> Resource.Error(resource.message ?: "Unknown error")
                is Resource.Loading -> Resource.Loading()
            }
        }
    }

    override fun getArtists(): Flow<Resource<List<ArtistInfo>>> {
        return getAllMediaItems().map { resource ->
            when (resource) {
                is Resource.Success -> {
                    val artists = resource.data?.groupBy { it.artist }?.map { (artistName, items) ->
                        ArtistInfo(
                            id = artistName,
                            name = artistName,
                            trackCount = items.size,
                            albumCount = items.map { it.album }.distinct().size,
                            artworkUri = items.first().artworkUri
                        )
                    } ?: emptyList()
                    Resource.Success(artists)
                }
                is Resource.Error -> Resource.Error(resource.message ?: "Unknown error")
                is Resource.Loading -> Resource.Loading()
            }
        }
    }

    override fun getMediaItemsByAlbum(albumId: String): Flow<Resource<List<MediaItem>>> {
        return mediaDao.getMediaItemsByAlbum(albumId)
            .map { entities ->
                Resource.Success(MediaMapper.toDomainList(entities))
            }
            .catch { e ->
                emit(Resource.Error("Failed to get album items: ${e.message}"))
            }
    }

    override fun getMediaItemsByArtist(artistId: String): Flow<Resource<List<MediaItem>>> {
        return mediaDao.getMediaItemsByArtist(artistId)
            .map { entities ->
                Resource.Success(MediaMapper.toDomainList(entities))
            }
            .catch { e ->
                emit(Resource.Error("Failed to get artist items: ${e.message}"))
            }
    }

    override fun searchMediaItems(query: String): Flow<Resource<List<MediaItem>>> {
        return mediaDao.searchMediaItems(query)
            .map { entities ->
                Resource.Success(MediaMapper.toDomainList(entities))
            }
            .catch { e ->
                emit(Resource.Error("Failed to search media items: ${e.message}"))
            }
    }

    override fun sortMediaItems(items: List<MediaItem>, sortOrder: SortOrder): List<MediaItem> {
        return when (sortOrder) {
            SortOrder.TITLE_ASC -> items.sortedBy { it.title }
            SortOrder.TITLE_DESC -> items.sortedByDescending { it.title }
            SortOrder.ARTIST_ASC -> items.sortedBy { it.artist }
            SortOrder.ARTIST_DESC -> items.sortedByDescending { it.artist }
            SortOrder.ALBUM_ASC -> items.sortedBy { it.album }
            SortOrder.ALBUM_DESC -> items.sortedByDescending { it.album }
            SortOrder.DURATION_ASC -> items.sortedBy { it.duration }
            SortOrder.DURATION_DESC -> items.sortedByDescending { it.duration }
            SortOrder.DATE_ADDED_ASC -> items.sortedBy { it.dateAdded }
            SortOrder.DATE_ADDED_DESC -> items.sortedByDescending { it.dateAdded }
        }
    }

    override suspend fun scanMediaFiles(): Resource<Unit> {
        return try {
            // This would be implemented with actual media scanning logic
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Error("Failed to scan media files: ${e.message}")
        }
    }

    override suspend fun refreshMediaLibrary(): Resource<Unit> {
        return try {
            // This would be implemented with actual refresh logic
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Error("Failed to refresh media library: ${e.message}")
        }
    }
}

