package com.tinhtx.player.playback

import android.content.Context
import androidx.media3.common.MediaItem
import androidx.media3.common.Player
import androidx.media3.exoplayer.ExoPlayer
import androidx.media3.exoplayer.source.MediaSource
import androidx.media3.exoplayer.source.ProgressiveMediaSource
import androidx.media3.datasource.DefaultDataSource
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class ExoPlayerManager @Inject constructor(
    private val context: Context
) {
    private val _exoPlayer = ExoPlayer.Builder(context).build()
    val exoPlayer: ExoPlayer = _exoPlayer

    private val _isPlaying = MutableStateFlow(false)
    val isPlaying: StateFlow<Boolean> = _isPlaying.asStateFlow()

    private val _currentPosition = MutableStateFlow(0L)
    val currentPosition: StateFlow<Long> = _currentPosition.asStateFlow()

    private val _duration = MutableStateFlow(0L)
    val duration: StateFlow<Long> = _duration.asStateFlow()

    init {
        _exoPlayer.addListener(object : Player.Listener {
            override fun onIsPlayingChanged(isPlaying: Boolean) {
                _isPlaying.value = isPlaying
            }

            override fun onPlaybackStateChanged(playbackState: Int) {
                if (playbackState == Player.STATE_READY) {
                    _duration.value = _exoPlayer.duration
                }
            }
        })

        // Update current position periodically
        startPositionUpdates()
    }

    fun playMedia(uri: String) {
        val mediaItem = MediaItem.fromUri(uri)
        val mediaSource = createMediaSource(mediaItem)
        _exoPlayer.setMediaSource(mediaSource)
        _exoPlayer.prepare()
        _exoPlayer.play()
    }

    fun pause() {
        _exoPlayer.pause()
    }

    fun resume() {
        _exoPlayer.play()
    }

    fun stop() {
        _exoPlayer.stop()
    }

    fun seekTo(position: Long) {
        _exoPlayer.seekTo(position)
    }

    fun skipToNext() {
        if (_exoPlayer.hasNextMediaItem()) {
            _exoPlayer.seekToNext()
        }
    }

    fun skipToPrevious() {
        if (_exoPlayer.hasPreviousMediaItem()) {
            _exoPlayer.seekToPrevious()
        }
    }

    fun setPlaylist(mediaItems: List<MediaItem>) {
        _exoPlayer.setMediaItems(mediaItems)
        _exoPlayer.prepare()
    }

    fun release() {
        _exoPlayer.release()
    }

    private fun createMediaSource(mediaItem: MediaItem): MediaSource {
        val dataSourceFactory = DefaultDataSource.Factory(context)
        return ProgressiveMediaSource.Factory(dataSourceFactory)
            .createMediaSource(mediaItem)
    }

    private fun startPositionUpdates() {
        // This would typically use a coroutine or timer to update position
        // For simplicity, we'll use a basic approach
        object : Runnable {
            override fun run() {
                if (_exoPlayer.isPlaying) {
                    _currentPosition.value = _exoPlayer.currentPosition
                }
                android.os.Handler(android.os.Looper.getMainLooper()).postDelayed(this, 1000)
            }
        }.run()
    }
}

package com.tinhtx.player.playback

import android.app.Notification
import android.app.NotificationChannel
import android.app.NotificationManager
import android.content.Context
import android.os.Build
import androidx.core.app.NotificationCompat
import androidx.media3.session.MediaSession
import androidx.media3.session.MediaSessionService
import com.tinhtx.player.common.Constants
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class MediaNotificationManager @Inject constructor(
    private val context: Context
) {
    private val notificationManager = context.getSystemService(Context.NOTIFICATION_SERVICE) as NotificationManager

    init {
        createNotificationChannel()
    }

    fun showNotification(service: MediaSessionService, mediaSession: MediaSession) {
        val notification = createNotification(mediaSession)
        service.startForeground(Constants.NOTIFICATION_ID, notification)
    }

    fun hideNotification() {
        notificationManager.cancel(Constants.NOTIFICATION_ID)
    }

    private fun createNotification(mediaSession: MediaSession): Notification {
        return NotificationCompat.Builder(context, Constants.NOTIFICATION_CHANNEL_ID)
            .setContentTitle("Now Playing")
            .setContentText("Music Player")
            .setSmallIcon(android.R.drawable.ic_media_play)
            .setOngoing(true)
            .setVisibility(NotificationCompat.VISIBILITY_PUBLIC)
            .build()
    }

    private fun createNotificationChannel() {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            val channel = NotificationChannel(
                Constants.NOTIFICATION_CHANNEL_ID,
                "Media Playback",
                NotificationManager.IMPORTANCE_LOW
            ).apply {
                description = "Media playback controls"
                setShowBadge(false)
            }
            notificationManager.createNotificationChannel(channel)
        }
    }
}

package com.tinhtx.player.playback

import android.app.PendingIntent
import android.content.Intent
import androidx.media3.common.MediaItem
import androidx.media3.common.Player
import androidx.media3.exoplayer.ExoPlayer
import androidx.media3.session.MediaSession
import androidx.media3.session.MediaSessionService
import dagger.hilt.android.AndroidEntryPoint
import javax.inject.Inject

@AndroidEntryPoint
class PlaybackService : MediaSessionService() {

    @Inject
    lateinit var exoPlayer: ExoPlayer

    @Inject
    lateinit var mediaNotificationManager: MediaNotificationManager

    private var mediaSession: MediaSession? = null

    override fun onCreate() {
        super.onCreate()

        val sessionActivityPendingIntent = packageManager?.getLaunchIntentForPackage(packageName)?.let { sessionIntent ->
            PendingIntent.getActivity(this, 0, sessionIntent, PendingIntent.FLAG_IMMUTABLE)
        }

        mediaSession = MediaSession.Builder(this, exoPlayer)
            .setSessionActivity(sessionActivityPendingIntent)
            .build()

        exoPlayer.addListener(object : Player.Listener {
            override fun onPlaybackStateChanged(playbackState: Int) {
                when (playbackState) {
                    Player.STATE_READY -> {
                        mediaNotificationManager.showNotification(this@PlaybackService, mediaSession!!)
                    }
                    Player.STATE_ENDED -> {
                        mediaNotificationManager.hideNotification()
                    }
                }
            }
        })
    }

    override fun onGetSession(controllerInfo: MediaSession.ControllerInfo): MediaSession? {
        return mediaSession
    }

    override fun onDestroy() {
        mediaSession?.run {
            player.release()
            release()
            mediaSession = null
        }
        mediaNotificationManager.hideNotification()
        super.onDestroy()
    }
}

package com.tinhtx.player.scanner
import android.content.ContentResolver import android.content.Context import android.database.Cursor import android.provider.MediaStore import com.tinhtx.player.model.MediaItem import com.tinhtx.player.model.MediaType import kotlinx.coroutines.Dispatchers import kotlinx.coroutines.withContext import javax.inject.Inject import javax.inject.Singleton
@Singleton class MediaStoreScanner @Inject constructor( private val context: Context ) { private val contentResolver: ContentResolver = context.contentResolver
suspend fun scanAudioFiles(): List<MediaItem> = withContext(Dispatchers.IO) { val audioFiles = mutableListOf<MediaItem>()
val projection = arrayOf( MediaStore.Audio.Media._ID, MediaStore.Audio.Media.TITLE, MediaStore.Audio.Media.ARTIST, MediaStore.Audio.Media.ALBUM, MediaStore.Audio.Media.DURATION, MediaStore.Audio.Media.DATA, MediaStore.Audio.Media.SIZE, MediaStore.Audio.Media.DATE_ADDED, MediaStore.Audio.Media.MIME_TYPE )
val selection = "${MediaStore.Audio.Media.IS_MUSIC} = 1" val sortOrder = "${MediaStore.Audio.Media.TITLE} ASC"
contentResolver.query( MediaStore.Audio.Media.EXTERNAL_CONTENT_URI, projection, selection, null, sortOrder )?.use { cursor -> val idColumn = cursor.getColumnIndexOrThrow(MediaStore.Audio.Media._ID) val titleColumn = cursor.getColumnIndexOrThrow(MediaStore.Audio.Media.TITLE) val artistColumn = cursor.getColumnIndexOrThrow(MediaStore.Audio.Media.ARTIST) val albumColumn = cursor.getColumnIndexOrThrow(MediaStore.Audio.Media.ALBUM) val durationColumn = cursor.getColumnIndexOrThrow(MediaStore.Audio.Media.DURATION) val dataColumn = cursor.getColumnIndexOrThrow(MediaStore.Audio.Media.DATA) val sizeColumn = cursor.getColumnIndexOrThrow(MediaStore.Audio.Media.SIZE) val dateAddedColumn = cursor.getColumnIndexOrThrow(MediaStore.Audio.Media.DATE_ADDED) val mimeTypeColumn = cursor.getColumnIndexOrThrow(MediaStore.Audio.Media.MIME_TYPE)
while (cursor.moveToNext()) { val id = cursor.getLong(idColumn).toString() val title = cursor.getString(titleColumn) ?: "Unknown" val artist = cursor.getString(artistColumn) ?: "Unknown Artist" val album = cursor.getString(albumColumn) ?: "Unknown Album" val duration = cursor.getLong(durationColumn) val path = cursor.getString(dataColumn) ?: "" val size = cursor.getLong(sizeColumn) val dateAdded = cursor.getLong(dateAddedColumn) * 1000 // Convert to milliseconds val mimeType = cursor.getString(mimeTypeColumn) ?: ""
val uri = "${MediaStore.Audio.Media.EXTERNAL_CONTENT_URI}/$id"
audioFiles.add( MediaItem( id = id, title = title, artist = artist, album = album, duration = duration, uri = uri, mediaType = MediaType.AUDIO, size = size, dateAdded = dateAdded, path = path, mimeType = mimeType ) ) } }
audioFiles }
suspend fun scanVideoFiles(): List<MediaItem> = withContext(Dispatchers.IO) { val videoFiles = mutableListOf<MediaItem>()
val projection = arrayOf( MediaStore.Video.Media._ID, MediaStore.Video.Media.TITLE, MediaStore.Video.Media.ARTIST, MediaStore.Video.Media.ALBUM, MediaStore.Video.Media.DURATION, MediaStore.Video.Media.DATA, MediaStore.Video.Media.SIZE, MediaStore.Video.Media.DATE_ADDED, MediaStore.Video.Media.MIME_TYPE )
val sortOrder = "${MediaStore.Video.Media.TITLE} ASC"
contentResolver.query( MediaStore.Video.Media.EXTERNAL_CONTENT_URI, projection, null, null, sortOrder )?.use { cursor -> val idColumn = cursor.getColumnIndexOrThrow(MediaStore.Video.Media._ID) val titleColumn = cursor.getColumnIndexOrThrow(MediaStore.Video.Media.TITLE) val artistColumn = cursor.getColumnIndexOrThrow(MediaStore.Video.Media.ARTIST) val albumColumn = cursor.getColumnIndexOrThrow(MediaStore.Video.Media.ALBUM) val durationColumn = cursor.getColumnIndexOrThrow(MediaStore.Video.Media.DURATION) val dataColumn = cursor.getColumnIndexOrThrow(MediaStore.Video.Media.DATA) val sizeColumn = cursor.getColumnIndexOrThrow(MediaStore.Video.Media.SIZE) val dateAddedColumn = cursor.getColumnIndexOrThrow(MediaStore.Video.Media.DATE_ADDED) val mimeTypeColumn = cursor.getColumnIndexOrThrow(MediaStore.Video.Media.MIME_TYPE)
while (cursor.moveToNext()) { val id = cursor.getLong(idColumn).toString() val title = cursor.getString(titleColumn) ?: "Unknown" val artist = cursor.getString(artistColumn) ?: "Unknown" val album = cursor.getString(albumColumn) ?: "Unknown" val duration = cursor.getLong(durationColumn) val path = cursor.getString(dataColumn) ?: "" val size = cursor.getLong(sizeColumn) val dateAdded = cursor.getLong(dateAddedColumn) * 1000 val mimeType = cursor.getString(mimeTypeColumn) ?: ""
val uri = "${MediaStore.Video.Media.EXTERNAL_CONTENT_URI}/$id"
videoFiles.add( MediaItem( id = id, title = title, artist = artist, album = album, duration = duration, uri = uri, mediaType = MediaType.VIDEO, size = size, dateAdded = dateAdded, path = path, mimeType = mimeType ) ) } }
videoFiles }
suspend fun scanAllMediaFiles(): List<MediaItem> { return scanAudioFiles() + scanVideoFiles() } }

package com.tinhtx.player.component.common

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material3.*
import androidx.compose.runtime.Composable
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.layout.ContentScale
import androidx.compose.ui.text.style.TextOverflow
import androidx.compose.ui.unit.dp
import coil.compose.AsyncImage
import com.tinhtx.player.model.MediaItem

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun MediaItemCard(
    mediaItem: MediaItem,
    onClick: (MediaItem) -> Unit,
    modifier: Modifier = Modifier
) {
    Card(
        onClick = { onClick(mediaItem) },
        modifier = modifier
            .fillMaxWidth()
            .height(80.dp),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surface
        )
    ) {
        Row(
            modifier = Modifier
                .fillMaxSize()
                .padding(12.dp),
            verticalAlignment = Alignment.CenterVertically
        ) {
            // Album artwork
            AsyncImage(
                model = mediaItem.artworkUri,
                contentDescription = "Album artwork",
                modifier = Modifier
                    .size(56.dp)
                    .clip(RoundedCornerShape(8.dp)),
                contentScale = ContentScale.Crop
            )

            Spacer(modifier = Modifier.width(12.dp))

            // Media info
            Column(
                modifier = Modifier.weight(1f)
            ) {
                Text(
                    text = mediaItem.title,
                    style = MaterialTheme.typography.titleSmall,
                    color = MaterialTheme.colorScheme.onSurface,
                    maxLines = 1,
                    overflow = TextOverflow.Ellipsis
                )

                Text(
                    text = "${mediaItem.artist}  ${mediaItem.album}",
                    style = MaterialTheme.typography.bodyMedium,
                    color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f),
                    maxLines = 1,
                    overflow = TextOverflow.Ellipsis
                )
            }

            // Duration
            Text(
                text = formatDuration(mediaItem.duration),
                style = MaterialTheme.typography.bodySmall,
                color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.5f)
            )
        }
    }
}

private fun formatDuration(duration: Long): String {
    val minutes = duration / 1000 / 60
    val seconds = (duration / 1000) % 60
    return String.format("%d:%02d", minutes, seconds)
}

package com.tinhtx.player.component.common

import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.platform.LocalContext
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.unit.dp
import androidx.activity.compose.rememberLauncherForActivityResult
import androidx.activity.result.contract.ActivityResultContracts
import com.tinhtx.player.util.PermissionUtils

@Composable
fun PermissionHandler(
    onPermissionsGranted: () -> Unit,
    onPermissionsDenied: () -> Unit,
    modifier: Modifier = Modifier
) {
    val context = LocalContext.current
    var showRationale by remember { mutableStateOf(false) }

    val permissionLauncher = rememberLauncherForActivityResult(
        contract = ActivityResultContracts.RequestMultiplePermissions()
    ) { permissions ->
        val allGranted = permissions.values.all { it }
        if (allGranted) {
            onPermissionsGranted()
        } else {
            showRationale = true
            onPermissionsDenied()
        }
    }

    LaunchedEffect(Unit) {
        if (PermissionUtils.hasRequiredPermissions(context)) {
            onPermissionsGranted()
        } else {
            permissionLauncher.launch(PermissionUtils.REQUIRED_PERMISSIONS)
        }
    }

    if (showRationale) {
        Card(
            modifier = modifier
                .fillMaxWidth()
                .padding(16.dp),
            colors = CardDefaults.cardColors(
                containerColor = MaterialTheme.colorScheme.errorContainer
            )
        ) {
            Column(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(16.dp),
                horizontalAlignment = Alignment.CenterHorizontally
            ) {
                Text(
                    text = "Storage Permission Required",
                    style = MaterialTheme.typography.titleMedium,
                    color = MaterialTheme.colorScheme.onErrorContainer,
                    textAlign = TextAlign.Center
                )

                Spacer(modifier = Modifier.height(8.dp))

                Text(
                    text = "This app needs access to your device storage to play music and videos. Please grant the permission to continue.",
                    style = MaterialTheme.typography.bodyMedium,
                    color = MaterialTheme.colorScheme.onErrorContainer,
                    textAlign = TextAlign.Center
                )

                Spacer(modifier = Modifier.height(16.dp))

                Button(
                    onClick = {
                        permissionLauncher.launch(PermissionUtils.REQUIRED_PERMISSIONS)
                        showRationale = false
                    },
                    colors = ButtonDefaults.buttonColors(
                        containerColor = MaterialTheme.colorScheme.primary
                    )
                ) {
                    Text("Grant Permission")
                }
            }
        }
    }
}

package com.tinhtx.player.component.common

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.text.KeyboardActions
import androidx.compose.foundation.text.KeyboardOptions
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.Clear
import androidx.compose.material.icons.filled.Search
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Modifier
import androidx.compose.ui.platform.LocalSoftwareKeyboardController
import androidx.compose.ui.text.input.ImeAction
import androidx.compose.ui.unit.dp

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun SearchBar(
    query: String,
    onQueryChange: (String) -> Unit,
    onSearch: (String) -> Unit,
    placeholder: String = "Search music...",
    modifier: Modifier = Modifier
) {
    val keyboardController = LocalSoftwareKeyboardController.current

    OutlinedTextField(
        value = query,
        onValueChange = onQueryChange,
        placeholder = {
            Text(
                text = placeholder,
                style = MaterialTheme.typography.bodyMedium
            )
        },
        leadingIcon = {
            Icon(
                imageVector = Icons.Default.Search,
                contentDescription = "Search",
                tint = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.6f)
            )
        },
        trailingIcon = {
            if (query.isNotEmpty()) {
                IconButton(
                    onClick = { onQueryChange("") }
                ) {
                    Icon(
                        imageVector = Icons.Default.Clear,
                        contentDescription = "Clear",
                        tint = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.6f)
                    )
                }
            }
        },
        keyboardOptions = KeyboardOptions(
            imeAction = ImeAction.Search
        ),
        keyboardActions = KeyboardActions(
            onSearch = {
                onSearch(query)
                keyboardController?.hide()
            }
        ),
        singleLine = true,
        modifier = modifier
            .fillMaxWidth()
            .padding(horizontal = 16.dp),
        colors = OutlinedTextFieldDefaults.colors(
            focusedBorderColor = MaterialTheme.colorScheme.primary,
            unfocusedBorderColor = MaterialTheme.colorScheme.outline.copy(alpha = 0.5f)
        )
    )
}

